import React,{useState} from 'react';
import {Form,Button, Container,Row,Col} from 'react-bootstrap';
import {Link,useNavigate} from 'react-router-dom';
import { login } from '../axios/index';

function AutoScreen({setUser}) {

  const navigate = useNavigate();

  const [formData,setFormData] = useState({
    email:"",
    password:""
  });

  return (
    <div>

     <Container bg="dark">
      <Row xs={12} md={7} className="justify-content-center align-items-center">
        <Col>
           <Form onSubmit={(e) => {
             e.preventDefault();

             login(formData)
               .then(res => {
                 localStorage.setItem("user",JSON.stringify(res.data));
                 setUser(res.data.message);
                 navigate("/");
               })
               .catch(err => {
                 setUser(err.response.data.message);
                 navigate("/");
               });

           }} className="container my-2" expand="lg">
             <Form.Group>
               <Form.Label htmlFor="Email">Email:</Form.Label>
               <Form.Control type="email" onChange={(e) => setFormData({...formData,email:e.target.value})} name="Email" placeholder='Email'/>
             </Form.Group>

             <Form.Group>
               <Form.Label htmlFor="password">Password:</Form.Label>
               <Form.Control type="password" onChange={(e) => setFormData({...formData,password:e.target.value})} name='password' placeholder='Password'/>
             </Form.Group>
        
               <Form.Group className='d-grid'>
                  <Button type='submit' size="lg" className='mt-2 text-center' variant="success" expand="lg">Sign in</Button>
                  <Form.Text className='text-center'>Don't have an account? <Link to="/signup">Sign up</Link></Form.Text>
               </Form.Group>
           </Form>
        </Col>
      </Row>
    </Container>
    {user}
  </div>
  )
}

export default AutoScreen;
