import React, { useEffect,useState } from 'react';
import axios from 'axios';

function App() {

  const [todo,setTodo] = useState(["Hey","Alişan","Ne Haber"]);

  useEffect(() => {
     getData();
  },[])

  const getData = async () => {
    try {
      const content = axios.get("http://localhost:8080/todos/all");
      const result = (await content).data;
      setTodo(result);
    } catch (error) {
      console.log(error);
    }
  }


  return (
    <div className='card'>
      <form>
        <input type="text" />
        <button type='submit'>Add</button>
      </form>
      {
        todo.map(item => {
          return(
            <div>
            <ul>
              <li>
              <p>{item.todo}</p>
               <div className="icons">
                <i className='fas fa-trash'></i>
                <i className='fas fa-square'></i>
               </div>
              </li>
             </ul>
            </div>
          )
        })
      }
    </div>
  )
}

export default App
