import React, { useEffect,useState } from 'react';
import axios from 'axios';

function App() {

  const [content,setContent] = useState([]);
  const [todo,setTodo] = useState([]);

  useEffect(() => {
     getData();
  },[])

  const getData = async () => {
    try {
      const content = axios.get("http://localhost:8080/todos/all");
      const result = (await content).data;
      setTodo(result);
    } catch (error) {
      console.log(error);
    }
  }


  return (
    <div className='card'>
      <h2>Todo List</h2>
      <form onSubmit={e => {
        e.preventDefault();
        const url = axios.post("http://localhost:8080/todos");
      }
      }>
        <input type="text" placeholder='Enter todo...' onChange={e => setContent(e.target.value)} value={content}/>
        <button type='submit'>Add</button>
      </form>
      <ul>
      {
        todo.map(item => {
          return(
            <li>
              {item.todo}
               <div className="icons">
                <i className='fas fa-trash'></i>
                <i className='fas fa-square'></i>
               </div>
             </li>
          )
        })
      }
      </ul>
    </div>
  )
}

export default App
